mapper.select:
	select 

  from TB_D_MENU
  where DELMARK = 0 

      and MENUNAME = #{menuname,jdbcType=VARCHAR}


      and MENUICON = #{menuicon,jdbcType=VARCHAR}


      and PARENTID = #{parentid,jdbcType=INTEGER}


      and TARGET = #{target,jdbcType=VARCHAR}


      and MENUHREF = #{menuhref,jdbcType=VARCHAR}


      and ISSHOW = #{isshow,jdbcType=INTEGER}


      and SORTCODE = #{sortcode,jdbcType=INTEGER}


      and ALLOWDEL = #{allowdel,jdbcType=INTEGER}


      and ALLOWEDIT = #{allowedit,jdbcType=INTEGER}


      and REMARKS = #{remarks,jdbcType=VARCHAR}


      and CREATETIME = #{createtime,jdbcType=TIMESTAMP}


      and CREATEUSERID = #{createuserid,jdbcType=INTEGER}


      and MODIFYTIME = #{modifytime,jdbcType=TIMESTAMP}


      and MODIFYUSERID = #{modifyuserid,jdbcType=INTEGER}
