mapper.sql:
select d.ID, d.DEPTNAME, d.DEPTNO, d.PARENTID, d.SORTCODE, d.REMARKS, d.DELMARK, d.CREATETIME, d.CREATEUSERID, d.MODIFYTIME, d.MODIFYUSERID,d.WECHATSTATUS, (case d.WECHATSTATUS when 0 then '未同步' when 1 then '已同步' when 2 then '更新失败' when 3 then '更新成功' else '' end) WECHATSTATUSLABEL ,(select p.DEPTNAME from TB_D_DEPARTMENT p where p.id=d.PARENTID ) PARENTIDLEBAL from TB_D_DEPARTMENT d where DELMARK = 0
and DEPTNAME = #{deptname,jdbcType=VARCHAR}
and DEPTNO = #{deptno,jdbcType=VARCHAR}
and PARENTID = #{parentid,jdbcType=INTEGER}
order by SORTCODE ,CREATETIME asc
