/*Table structure for table `sp_case` */

DROP TABLE IF EXISTS `sp_case`;

CREATE TABLE `sp_case` (
  `ID` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `case_name` varchar(200) DEFAULT NULL COMMENT '案例名称',
  `plan` varchar(200) DEFAULT NULL COMMENT '定制方案',
  `cost` decimal(10,2) DEFAULT NULL COMMENT '定制费用:0-保密',
  `customer_name` varchar(200) DEFAULT NULL COMMENT '客户名称',
  `industry_id` bigint(20) unsigned NOT NULL COMMENT '行业ID',
  `category_id` bigint(20) unsigned NOT NULL COMMENT '所属品类',
  `sp_id` bigint(20) NOT NULL COMMENT '服务商ID',
  `description` text COMMENT '案例描述',
  `customer_evaluation` varchar(500) DEFAULT NULL COMMENT '客户评价',
  `custom_num` int(11) DEFAULT NULL COMMENT '定制数量',
  `custom_cycle` int(11) DEFAULT NULL COMMENT '定制周期(天数)',
  `create_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  `is_del` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否删除',
  PRIMARY KEY (`ID`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='案例表';

